INSERT INTO `program_shop-1425` VALUES (195, 9, 166, '', '眷村料理', '“每个眷村都有一个村子口”，老板王怀民说。”村子口是个大目标，开店（村子口）就是为了让大家能找回属於眷村的回忆。“', 2, '', '/images/台湾食堂/1.7.png;/images/台湾食堂/1.8.png;/images/台湾食堂/1.9.png', NULL, '/images/台湾食堂/thumb/t_1.7.jpg;/images/台湾食堂/thumb/t_1.8.jpg;/images/台湾食堂/thumb/t_1.9.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (199, 9, 170, '', '川菜', '内地来的曹淳瑄说她刚来台湾的时候饿了半年的肚子，为了一解乡愁和填饱自己，完全不会做菜的她来了台湾才学会怎么拿菜刀，经过十多年的努力，现在经营的”川国演义“已成为高雄川菜的地标。', 3, '', '/images/台湾食堂/3.1.png;/images/台湾食堂/3.2.png;/images/台湾食堂/3.3.png', NULL, '/images/台湾食堂/thumb/t_3.1.jpg;/images/台湾食堂/thumb/t_3.2.jpg;/images/台湾食堂/thumb/t_3.3.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (200, 9, 171, '', '越南美食', '越南妻子们中午人手一只鸭仔蛋，说着台湾人听不懂的越南话，讨论家乡生活和台湾的区别，然后取笑自己的老公不敢吃鸭仔蛋，对自己小孩一餐可以吃四个感到无比自豪，这群越南来的新住民在汐止找到家的归属感。', 3, '', '/images/台湾食堂/3.4.png;/images/台湾食堂/3.5.png;/images/台湾食堂/3.6.png', NULL, '/images/台湾食堂/thumb/t_3.4.jpg;/images/台湾食堂/thumb/t_3.5.jpg;/images/台湾食堂/thumb/t_3.6.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (201, 9, 172, '', '欧式披萨', '一眼就看得出来Chris和平常的披萨厨师有很大的不同，欧洲帅哥的他在朋友的邀约下让他只身来到台湾，组装了一台披萨车后开始了他的台湾生活。', 3, '', '/images/台湾食堂/3.7.png;/images/台湾食堂/3.8.png;/images/台湾食堂/3.9.png', NULL, '/images/台湾食堂/thumb/t_3.7.jpg;/images/台湾食堂/thumb/t_3.8.jpg;/images/台湾食堂/thumb/t_3.9.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (202, 9, 173, '', '牛肉面', '你知道一碗三千块的牛肉面怎么卖？在这里三千块一碗还不是最高级，上万元的牛肉面味道又是如何？', 4, '', '/images/台湾食堂/4.1.png;/images/台湾食堂/4.2.png;/images/台湾食堂/4.9.png', NULL, '/images/台湾食堂/thumb/t_4.1.jpg;/images/台湾食堂/thumb/t_4.2.jpg;/images/台湾食堂/thumb/t_4.9.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (203, 9, 174, '', '牛肉面', '傅老师家的牛肉面，香！这样浓郁的香味里面有桂枝、大红袍、青花椒、桂皮、沙姜、白茅根、百里香、土耳其俄力冈叶等四十多种香草，如此一来才有了极致的香气！', 4, '', '/images/台湾食堂/4.3.png;/images/台湾食堂/4.4.png;/images/台湾食堂/4.10.png', NULL, '/images/台湾食堂/thumb/t_4.3.jpg;/images/台湾食堂/thumb/t_4.4.jpg;/images/台湾食堂/thumb/t_4.10.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (204, 9, 175, '', '牛肉面', '林二姐说，爸爸以前是「闭着眼睛吃牛肉、卖牛肉面的，因为最早台湾人不吃牛，但为了四姐妹的学费，所以有了这碗满满爱的牛肉面」。', 4, '', '/images/台湾食堂/4.5.png;/images/台湾食堂/4.6.png;/images/台湾食堂/4.12.png', NULL, '/images/台湾食堂/thumb/t_4.5.jpg;/images/台湾食堂/thumb/t_4.6.jpg;/images/台湾食堂/thumb/t_4.12.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (205, 9, 176, '', '牛肉面', '蔡妈妈说，「我们这碗面可以带给大家很多的回味，所以再怎么辛苦怎么忙，我们都会在这坚持下去…」', 4, '', '/images/台湾食堂/4.7.png;/images/台湾食堂/4.8.png;/images/台湾食堂/4.11.png', NULL, '/images/台湾食堂/thumb/t_4.7.jpg;/images/台湾食堂/thumb/t_4.8.jpg;/images/台湾食堂/thumb/t_4.11.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (206, 9, 177, '', '福州小吃', '基隆庙口夜市的特色在于这里的摊贩是不流动的。庙口的摊子基本都有三代历史。百年吴家鼎边趖的老板吴吉祥告诉你，为什么台湾小吃大部份都是从庙口开始发迹。', 5, '', '/images/台湾食堂/5.1.png;/images/台湾食堂/5.2.png;/images/台湾食堂/5.3.png', NULL, '/images/台湾食堂/thumb/t_5.1.jpg;/images/台湾食堂/thumb/t_5.2.jpg;/images/台湾食堂/thumb/t_5.3.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (207, 9, 178, '', '北斗特色小吃', '北斗肉圆的起源是百年前一场将作物全部摧毁的大水，剩下地瓜粉的心灵手巧范氏先祖创造出肉圆的雏形，经过了四代的改良，肉圆创始者的后人范瑞铭做着这样最道地的北斗小吃。', 5, '', '/images/台湾食堂/5.4.png;/images/台湾食堂/5.5.png;/images/台湾食堂/5.12.png', NULL, '/images/台湾食堂/thumb/t_5.4.jpg;/images/台湾食堂/thumb/t_5.5.jpg;/images/台湾食堂/thumb/t_5.12.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (208, 9, 179, '', '古早味烤玉米', '由花莲吉安的林上哲告诉你，从南美洲引进台湾的玉米，怎么会变成台湾小吃的烧番卖。', 5, '', '/images/台湾食堂/5.6.png;/images/台湾食堂/5.7.png;/images/台湾食堂/5.13.png', NULL, '/images/台湾食堂/thumb/t_5.6.jpg;/images/台湾食堂/thumb/t_5.7.jpg;/images/台湾食堂/thumb/t_5.13.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (209, 9, 180, '', '棺材板创始店', '台南沙卡里巴是一个从日治时代就存在的市集，这里承载的是横越几个世代的记忆。棺材板传人许永安要告诉你这个有着台怪名字的西式小吃从哪里来，又会往哪里去？', 5, '', '/images/台湾食堂/5.8.png;/images/台湾食堂/5.9.png;/images/台湾食堂/5.14.png', NULL, '/images/台湾食堂/thumb/t_5.8.jpg;/images/台湾食堂/thumb/t_5.9.jpg;/images/台湾食堂/thumb/t_5.14.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (210, 9, 181, '', '臭豆腐', '深坑的臭豆腐为什麼这样有名？吃豆腐又有什么好处？家里三代都卖臭豆腐的张仁详说分明。', 5, '', '/images/台湾食堂/5.10.png;/images/台湾食堂/5.11.png;/images/台湾食堂/5.15.png', NULL, '/images/台湾食堂/thumb/t_5.10.jpg;/images/台湾食堂/thumb/t_5.11.jpg;/images/台湾食堂/thumb/t_5.15.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (211, 9, 182, '', '野菜', '花莲欣绿农庄的朱进丰大哥不只提供美食给旅客，他还带游客体验马太鞍湿地的部落文化。在这里，旅人可以亲自拔蔬菜，也可以下到河水中摸鱼捉虾。好吃又好玩，兼程了解了不同文化的生活方式。', 6, '', '/images/台湾食堂/6.1.png;/images/台湾食堂/6.2.png;/images/台湾食堂/6.13.png', NULL, '/images/台湾食堂/thumb/t_6.1.jpg;/images/台湾食堂/thumb/t_6.2.jpg;/images/台湾食堂/thumb/t_6.13.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (212, 9, 183, '', '港式烤鸡 烤乳猪', '土鸡城如果照着字面上的意思只卖鸡，那便老早被消费者淘汰掉了。在台中大坑有一个来自香港的大厨周国权，他秘制的烤乳猪放半天皮都还是脆的。他对土鸡城、休闲餐厅又有另一番演绎。', 6, '', '/images/台湾食堂/6.3.png;/images/台湾食堂/6.4.png;/images/台湾食堂/6.5.png;/images/台湾食堂/6.6.png', NULL, '/images/台湾食堂/thumb/t_6.3.jpg;/images/台湾食堂/thumb/t_6.4.jpg;/images/台湾食堂/thumb/t_6.5.jpg;/images/台湾食堂/thumb/t_6.6.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (213, 9, 184, '', '泥火山鸡', '高雄冈山有着台湾八景的月世界，头前园休闲餐厅的老板潘荣贵与您分享他小时候吃的克难泥火山鸡有着怎样的故事。', 6, '', '/images/台湾食堂/6.7.png;/images/台湾食堂/6.8.png;/images/台湾食堂/6.14.png', NULL, '/images/台湾食堂/thumb/t_6.7.jpg;/images/台湾食堂/thumb/t_6.8.jpg;/images/台湾食堂/thumb/t_6.14.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (214, 9, 185, '', '养生料理', '新店孟鸿养身坊的主人俞炳南要告诉大家他们一家，是怎么从深山里的养鸡户，变成名人都赞不绝口的养身坊。', 6, '', '/images/台湾食堂/6.9.png;/images/台湾食堂/6.10.png;/images/台湾食堂/6.11.png;/images/台湾食堂/6.12.png', NULL, '/images/台湾食堂/thumb/t_6.9.jpg;/images/台湾食堂/thumb/t_6.10.jpg;/images/台湾食堂/thumb/t_6.11.jpg;/images/台湾食堂/thumb/t_6.12.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (215, 9, 186, '', '海鲜', '台北海产店老板张金良每天台北、宜兰两地跑，就是为了给顾客最新鲜的海鲜。一年三百六十五天从不间断，海胆达人林志团的海胆便独家供应给张金良，但对于海鲜的供不应求，张大哥也只能摇摇头不去想它。', 7, '', '/images/台湾食堂/7.1.png;/images/台湾食堂/7.2.png;/images/台湾食堂/7.3.png;/images/台湾食堂/7.4.png;/images/台湾食堂/7.5.png', NULL, '/images/台湾食堂/thumb/t_7.1.jpg;/images/台湾食堂/thumb/t_7.2.jpg;/images/台湾食堂/thumb/t_7.3.jpg;/images/台湾食堂/thumb/t_7.4.jpg;/images/台湾食堂/thumb/t_7.5.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (216, 9, 187, '', '新鲜海鲜现捞', '垦丁的小渔港边有家全台最青的海产店，店面正前方五米有小小的港口，港口就是他的天然冰箱。岳父、岳母捕回来的鱼就养在渔港中，客人点了鱼，现捞起来马上料理。', 7, '', '/images/台湾食堂/7.6.png;/images/台湾食堂/7.7.png;/images/台湾食堂/7.8.png;/images/台湾食堂/7.9.png', NULL, '/images/台湾食堂/thumb/t_7.6.jpg;/images/台湾食堂/thumb/t_7.7.jpg;/images/台湾食堂/thumb/t_7.8.jpg;/images/台湾食堂/thumb/t_7.9.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (217, 9, 188, '', '手工板条', '手工板条温元玮告诉你，为什么全美浓只剩下他不用机器做粄条。', 8, '', '/images/台湾食堂/8.1.png;/images/台湾食堂/8.2.png;/images/台湾食堂/8.10.png', NULL, '/images/台湾食堂/thumb/t_8.1.jpg;/images/台湾食堂/thumb/t_8.2.jpg;/images/台湾食堂/thumb/t_8.10.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (218, 9, 189, '', '手工米食', '米食老板吴裕民认为没有添加剂的客家米食，才是真正妈妈的味道。', 8, '', '/images/台湾食堂/8.3.png;/images/台湾食堂/8.4.png;/images/台湾食堂/8.5.png;/images/台湾食堂/8.6.png', NULL, '/images/台湾食堂/thumb/t_8.3.jpg;/images/台湾食堂/thumb/t_8.4.jpg;/images/台湾食堂/thumb/t_8.5.jpg;/images/台湾食堂/thumb/t_8.6.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (219, 9, 190, '', '传统客家美食', '黄豆豆是离开城市结束朝九晚五生活，回到大溪乡下经营客家美食之后，才发现以前在台北生活的那一套在老家完全不管用，重新和婶婆学习的过程中，对人生有了一番新的体认。', 8, '', '/images/台湾食堂/8.7.png;/images/台湾食堂/8.8.png;/images/台湾食堂/8.9.png', NULL, '/images/台湾食堂/thumb/t_8.7.jpg;/images/台湾食堂/thumb/t_8.8.jpg;/images/台湾食堂/thumb/t_8.9.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (220, 9, 191, '', '汕头特色小吃', '广东汕头西天巷的邱淑英一辈子都在煎蚵仔，在那儿，蚵仔煎换了一个名“蠔烙”。', 9, '', '/images/台湾食堂/9.1.png;/images/台湾食堂/9.2.png;/images/台湾食堂/9.15.png', NULL, '/images/台湾食堂/thumb/t_9.1.jpg;/images/台湾食堂/thumb/t_9.2.jpg;/images/台湾食堂/thumb/t_9.15.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (221, 9, 192, '', '特色蚵卷', '台南安平的陈秀月从蚵农摇身一变成了卖蚵卷的老板娘，蚵卷是怎麼发明的？', 9, '', '/images/台湾食堂/9.3.png;/images/台湾食堂/9.4.png;/images/台湾食堂/9.5.png', NULL, '/images/台湾食堂/thumb/t_9.3.jpg;/images/台湾食堂/thumb/t_9.4.jpg;/images/台湾食堂/thumb/t_9.5.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (222, 9, 193, '', '蚵仔面线', '新北板桥的陈俊成认为，蚵仔面线之所以好吃，手工面线的制作绝对马虎不得。', 9, '', '/images/台湾食堂/9.6.png;/images/台湾食堂/9.7.png;/images/台湾食堂/9.16.png', NULL, '/images/台湾食堂/thumb/t_9.6.jpg;/images/台湾食堂/thumb/t_9.7.jpg;/images/台湾食堂/thumb/t_9.16.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (223, 9, 194, '', '蚵仔煎', '圆环可能是上一代台北最重要的地标，圆环蚵仔煎的老板赖炳勋从十几岁上台北卖蚵仔煎到现在六十几岁，他的蚵仔人生见证著圆环的繁华、落寞与再起。', 9, '', '/images/台湾食堂/9.8.png;/images/台湾食堂/9.9.png;/images/台湾食堂/9.17.png', NULL, '/images/台湾食堂/thumb/t_9.8.jpg;/images/台湾食堂/thumb/t_9.9.jpg;/images/台湾食堂/thumb/t_9.17.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (224, 9, 195, '', '古早味蚵仔煎', '台南国华路上的石经臼蚵仔煎，老板张大喜八十几岁，依旧用最传统的方式做蚵仔煎，「真正古早味的蚵仔煎会放肉燥和豆芽菜」', 9, '', '/images/台湾食堂/9.10.png;/images/台湾食堂/9.11.png;/images/台湾食堂/9.18.png', NULL, '/images/台湾食堂/thumb/t_9.10.jpg;/images/台湾食堂/thumb/t_9.11.jpg;/images/台湾食堂/thumb/t_9.18.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (225, 9, 196, '', '鲜蚵专卖', '彰化鹿港林丽鸿的公公是鹿港蚵仔煎的开基原祖，听她娓娓道来鹿港蚵仔煎的源起。', 9, '', '/images/台湾食堂/9.12.png;/images/台湾食堂/9.19.png;/images/台湾食堂/9.20.png', NULL, '/images/台湾食堂/thumb/t_9.12.jpg;/images/台湾食堂/thumb/t_9.19.jpg;/images/台湾食堂/thumb/t_9.20.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (226, 9, 197, '', '烤生蚝', '三年来的坚持，每一口最新鲜的生蚝在口中迸发汁水，都是对美味最虔诚的执著信仰', 9, '', '/images/台湾食堂/9.13.png;/images/台湾食堂/9.14.png;/images/台湾食堂/9.21..png', NULL, '/images/台湾食堂/thumb/t_9.13.jpg;/images/台湾食堂/thumb/t_9.14.jpg;/images/台湾食堂/thumb/t_9.21..jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (227, 9, 198, '', '传统福建菜', '福建聚春园第八代传人杨伟华告诉你为什么闽南喜庆中一定要有红蟳米糕？', 10, '', '/images/台湾食堂/10.1.png;/images/台湾食堂/10.2.png;/images/台湾食堂/10.3.png', NULL, '/images/台湾食堂/thumb/t_10.1.jpg;/images/台湾食堂/thumb/t_10.2.jpg;/images/台湾食堂/thumb/t_10.3.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (228, 9, 199, '', '道地台菜', '欣叶的老板李秀英说，台菜就是俗语说的「家常菜」，她是如何让煎猪肝、菜圃蛋这些庶民菜放在台北101还有人买单呢？', 10, '', '/images/台湾食堂/10.4.png;/images/台湾食堂/10.5.png;/images/台湾食堂/10.6.png;/images/台湾食堂/10.7.png;/images/台湾食堂/10.8.png;/images/台湾食堂/10.9.png;/images/台湾食堂/10.10.png', NULL, '/images/台湾食堂/thumb/t_10.4.jpg;/images/台湾食堂/thumb/t_10.5.jpg;/images/台湾食堂/thumb/t_10.6.jpg;/images/台湾食堂/thumb/t_10.7.jpg;/images/台湾食堂/thumb/t_10.8.jpg;/images/台湾食堂/thumb/t_10.9.jpg;/images/台湾食堂/thumb/t_10.10.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (229, 9, 200, '', '新台湾菜', '宜兰「麟手创」的总厨陈兆麟认为，台菜其实就是妈妈煮的家乡菜，台菜就在这代代相传中，既保留妈妈的味道，又不停融合新元素，成为独树一格的世界混合菜系。', 10, '', '/images/台湾食堂/10.11.png;/images/台湾食堂/10.12.png;/images/台湾食堂/10.13.png', NULL, '/images/台湾食堂/thumb/t_10.11.jpg;/images/台湾食堂/thumb/t_10.12.jpg;/images/台湾食堂/thumb/t_10.13.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (230, 9, 201, '', '凤梨酥 ', '「微热山丘」有着如同偶像剧般梦幻的名字，谁能想到其实是四个兄弟中年转业的创业品牌。创办人许铭仁表示，当初真的也没有想到「微热山丘」能成为凤梨酥的品牌领头羊，一个不喜欢吃甜食的男人，如何办到？', 13, '', '/images/台湾食堂/13.1.png;/images/台湾食堂/13.2.png;/images/台湾食堂/13.3.png;/images/台湾食堂/13.4.png', NULL, '/images/台湾食堂/thumb/t_13.1.jpg;/images/台湾食堂/thumb/t_13.2.jpg;/images/台湾食堂/thumb/t_13.3.jpg;/images/台湾食堂/thumb/t_13.4.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (231, 9, 202, '', '排湾族料理', '新竹内湾野姜花的中兴只是起了一个头，屏东的牡丹、宜兰的员山也都用野姜花让曾经失落的野姜花再度兴旺了起来，野姜花的美味献给那些曾经失落的一代', 14, '', '/images/台湾食堂/14.1.png;/images/台湾食堂/14.2.png;/images/台湾食堂/14.3.png;/images/台湾食堂/14.4.png;/images/台湾食堂/14.5.png', NULL, '/images/台湾食堂/thumb/t_14.1.jpg;/images/台湾食堂/thumb/t_14.2.jpg;/images/台湾食堂/thumb/t_14.3.jpg;/images/台湾食堂/thumb/t_14.4.jpg;/images/台湾食堂/thumb/t_14.5.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (232, 9, 203, '', '野姜花粽', '将野姜花剁碎糅合成馅料，包进一个个粽子中，这道看似平凡的小食，却是多少人魂牵梦绕的乡味。', 14, '', '/images/台湾食堂/14.6.png;/images/台湾食堂/14.7.png;/images/台湾食堂/14.8.png;/images/台湾食堂/14.9.png', NULL, '/images/台湾食堂/thumb/t_14.6.jpg;/images/台湾食堂/thumb/t_14.7.jpg;/images/台湾食堂/thumb/t_14.8.jpg;/images/台湾食堂/thumb/t_14.9.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (233, 9, 204, '', '客家料理', '人家说十年河东十年河西，内湾的辉煌不再，曾经在内湾广为使用的调味料野姜花被遗忘，然而近几年新竹内湾野姜花又开始兴起了', 14, '', '/images/台湾食堂/14.10.png;/images/台湾食堂/14.11.png;/images/台湾食堂/14.14.png', NULL, '/images/台湾食堂/thumb/t_14.10.jpg;/images/台湾食堂/thumb/t_14.11.jpg;/images/台湾食堂/thumb/t_14.14.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (234, 9, 205, '', '野姜花料理', '野姜花用其独特的魅力吸引着更多的人投入它的怀抱。', 14, '', '/images/台湾食堂/14.12.png;/images/台湾食堂/14.13.png;/images/台湾食堂/14.15.png', NULL, '/images/台湾食堂/thumb/t_14.12.jpg;/images/台湾食堂/thumb/t_14.13.jpg;/images/台湾食堂/thumb/t_14.15.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (235, 9, 206, '', '总统御厨料理', '因为古坑的环境不好，水蛙师（张和锦）从小就跑出来当学徒，他是怎么用家乡最原始单纯的食材为李前总统准备笋宴？', 15, '', '/images/台湾食堂/15.1.png;/images/台湾食堂/15.2.png;/images/台湾食堂/15.3.png;/images/台湾食堂/15.4.png;/images/台湾食堂/15.5.png', NULL, '/images/台湾食堂/thumb/t_15.1.jpg;/images/台湾食堂/thumb/t_15.2.jpg;/images/台湾食堂/thumb/t_15.3.jpg;/images/台湾食堂/thumb/t_15.4.jpg;/images/台湾食堂/thumb/t_15.5.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (236, 9, 207, '', '麻竹笋锉冰', '看似简单的麻竹，道出滋味丰富的人生百态，也在融合创新中焕发新的吸引力。', 15, '', '/images/台湾食堂/15.6.png;/images/台湾食堂/15.7.png;/images/台湾食堂/15.8.png', NULL, '/images/台湾食堂/thumb/t_15.6.jpg;/images/台湾食堂/thumb/t_15.7.jpg;/images/台湾食堂/thumb/t_15.8.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (237, 9, 208, '', '金针花料理', '在六十石山的花农萧文龙这么说着，只要谁家的山头开满花，谁一年的心血就白费了。而同时，观光带来的效益也冲击着美丽又纯朴的天堂，在农业和观光间该如何做取舍，这又是另一个故事。', 16, '', '/images/台湾食堂/16.1.png;/images/台湾食堂/16.2.png;/images/台湾食堂/16.3.png;/images/台湾食堂/16.4.png', NULL, '/images/台湾食堂/thumb/t_16.1.jpg;/images/台湾食堂/thumb/t_16.2.jpg;/images/台湾食堂/thumb/t_16.3.jpg;/images/台湾食堂/thumb/t_16.4.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (238, 9, 209, '', '金针花料理', '赤科山的周庆南一家人即使在风灾中屋顶被吹走，满山的金针化为乌有，仍然坚守著这片美丽的土地', 16, '', '/images/台湾食堂/16.5.png;/images/台湾食堂/16.6.png;/images/台湾食堂/16.7.png;/images/台湾食堂/16.8.png', NULL, '/images/台湾食堂/thumb/t_16.5.jpg;/images/台湾食堂/thumb/t_16.6.jpg;/images/台湾食堂/thumb/t_16.7.jpg;/images/台湾食堂/thumb/t_16.8.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (239, 9, 210, '', '风味料理', '黑黑的菜脯，为什么会是宝？在高雄美浓短短小小的有机菠萝又有什么特别？罗元鸿和妻子为什么会为了它放弃原本教育训练的工作回乡下当农夫', 17, '', '/images/台湾食堂/17.1.png;/images/台湾食堂/17.2.png;/images/台湾食堂/17.7.png', NULL, '/images/台湾食堂/thumb/t_17.1.jpg;/images/台湾食堂/thumb/t_17.2.jpg;/images/台湾食堂/thumb/t_17.7.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (240, 9, 211, '', '客家福菜', '芥菜有很多名字，闽南人称它常年菜，客家人叫他大菜。大菜如何变成酸菜、福菜、梅干菜？领着大家来到苗栗公馆见证福菜一系列的变化，实际感受它的巧。', 17, '', '/images/台湾食堂/17.3.png;/images/台湾食堂/17.4.png;/images/台湾食堂/17.8.png', NULL, '/images/台湾食堂/thumb/t_17.3.jpg;/images/台湾食堂/thumb/t_17.4.jpg;/images/台湾食堂/thumb/t_17.8.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (241, 9, 212, '', '客家桔酱', '客家人的桔儿不是一般的桔子，就这么长在新竹竹东的田头田尾，当地人说它很酸很难吃，节俭的客家人如何将这么特别的食材变成大师口中不得了的沾酱？', 17, '', '/images/台湾食堂/17.5.png;/images/台湾食堂/17.6.png;/images/台湾食堂/17.9.png', NULL, '/images/台湾食堂/thumb/t_17.5.jpg;/images/台湾食堂/thumb/t_17.6.jpg;/images/台湾食堂/thumb/t_17.9.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (242, 9, 213, '', '在来米米粉', '山川米粉的掌门媳妇曾玉贵说，现在米粉都是披着羊皮的狼，大部份的米粉其实是用玉米粉制作的，剩下为数不多的业者坚持传统，费时旷日的制作方法，为的只是保留最纯粹最真的饮食文化。', 18, '', '/images/台湾食堂/18.1.png;/images/台湾食堂/18.2.png;/images/台湾食堂/18.5.png', NULL, '/images/台湾食堂/thumb/t_18.1.jpg;/images/台湾食堂/thumb/t_18.2.jpg;/images/台湾食堂/thumb/t_18.5.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (243, 9, 214, '', '纯天然柿饼', '住在新埔旱坑里的刘珍珠这么说着：「一百七十年前当先人在这里落脚时并不晓得这里几乎不下雨，因为前面的小溪一年到头都是干的，后来就取名旱坑里。在这样艰苦的环境下，上天还是赏了我们一口饭吃」。耐旱的柿子加上九降风，有了这么一家三代与柿饼的故事。', 18, '', '/images/台湾食堂/18.3.png;/images/台湾食堂/18.4.png;/images/台湾食堂/18.6.png', NULL, '/images/台湾食堂/thumb/t_18.3.jpg;/images/台湾食堂/thumb/t_18.4.jpg;/images/台湾食堂/thumb/t_18.6.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (244, 9, 215, '', '自制酱油 精致铁板烧', '位在宜兰罗东的飨宴铁板烧，主厨程智勇每天亲自去码头挑选最新鲜的渔获，在他认为，再好的食材，如果没有好的调味，只是暴殄天物。原来他的秘密武器是自己酿制、有着老母香，从他曾祖父传到阿祖、阿嬷、母亲再交到他手中至少四代的家传酱油。', 19, '', '/images/台湾食堂/19.1.png;/images/台湾食堂/19.2.png;/images/台湾食堂/19.3.png;/images/台湾食堂/19.4.png;/images/台湾食堂/19.5.png', NULL, '/images/台湾食堂/thumb/t_19.1.jpg;/images/台湾食堂/thumb/t_19.2.jpg;/images/台湾食堂/thumb/t_19.3.jpg;/images/台湾食堂/thumb/t_19.4.jpg;/images/台湾食堂/thumb/t_19.5.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (245, 9, 216, '', '手工柴烧黑豆酱油', '蔡大哥收成的黑豆，最后要交到酱油达人谢裕读的手上。达人透露，古法酿造的酱油，最少需要两年熟成，而现代化学酱油整个制作过程只需要六到七天。', 19, '', '/images/台湾食堂/19.6.png;/images/台湾食堂/19.7.png;/images/台湾食堂/19.8.png;/images/台湾食堂/19.9.png', NULL, '/images/台湾食堂/thumb/t_19.6.jpg;/images/台湾食堂/thumb/t_19.7.jpg;/images/台湾食堂/thumb/t_19.8.jpg;/images/台湾食堂/thumb/t_19.9.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (246, 9, 217, '', '自然轻食', '花莲的张武增哪里来的灵感，将常见的野草油菊引进赤科山做大规模的种植？', 20, '', '/images/台湾食堂/20.1.png;/images/台湾食堂/20.2.png;/images/台湾食堂/20.3.png;/images/台湾食堂/20.4.png;/images/台湾食堂/20.5.png', NULL, '/images/台湾食堂/thumb/t_20.1.jpg;/images/台湾食堂/thumb/t_20.2.jpg;/images/台湾食堂/thumb/t_20.3.jpg;/images/台湾食堂/thumb/t_20.4.jpg;/images/台湾食堂/thumb/t_20.5.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (247, 9, 218, '', '以花入馔', '大厨陈樱美可以用花做出多少种料理？', 20, '', '/images/台湾食堂/20.6.png;/images/台湾食堂/20.7.png;/images/台湾食堂/20.8.png;/images/台湾食堂/20.9.png', NULL, '/images/台湾食堂/thumb/t_20.6.jpg;/images/台湾食堂/thumb/t_20.7.jpg;/images/台湾食堂/thumb/t_20.8.jpg;/images/台湾食堂/thumb/t_20.9.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (248, 9, 219, '', '桂花百年步道', '石碇的桂花达人陈智勤爬着阿公亲手种植，自小和他一起长大的桂花树，见他小心捧着小心采摘的桂花，桂花之于他有什么样的感情？', 20, '', '/images/台湾食堂/20.10.png;/images/台湾食堂/20.11.png;/images/台湾食堂/20.12.png', NULL, '/images/台湾食堂/thumb/t_20.10.jpg;/images/台湾食堂/thumb/t_20.11.jpg;/images/台湾食堂/thumb/t_20.12.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (249, 9, 220, '', '特色生菜', '南投的玫瑰达人章思广又如何用最自然的功法种植我们可以食用的玫瑰花？', 20, '', '/images/台湾食堂/20.13.png;/images/台湾食堂/20.14.png;/images/台湾食堂/20.15.png', NULL, '/images/台湾食堂/thumb/t_20.13.jpg;/images/台湾食堂/thumb/t_20.14.jpg;/images/台湾食堂/thumb/t_20.15.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (250, 9, 221, '', '梅子农特产', '梅子博物馆诉说着那些梅乡可能要被淡忘的故事。', 21, '', '/images/台湾食堂/21.1.png;/images/台湾食堂/21.2.png;/images/台湾食堂/21.3.png;/images/台湾食堂/21.4.png;/images/台湾食堂/21.5.png', NULL, '/images/台湾食堂/thumb/t_21.1.jpg;/images/台湾食堂/thumb/t_21.2.jpg;/images/台湾食堂/thumb/t_21.3.jpg;/images/台湾食堂/thumb/t_21.4.jpg;/images/台湾食堂/thumb/t_21.5.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (251, 9, 222, '', '道地梅花餐', '柳家梅园的柳庆祥将家族梅园赋予风雅的情趣，游客可以免费入园，获得的是更多精神方面的感受。', 21, '', '/images/台湾食堂/21.6.png;/images/台湾食堂/21.7.png;/images/台湾食堂/21.8.png;/images/台湾食堂/21.9.png;/images/台湾食堂/21.10.png', NULL, '/images/台湾食堂/thumb/t_21.6.jpg;/images/台湾食堂/thumb/t_21.7.jpg;/images/台湾食堂/thumb/t_21.8.jpg;/images/台湾食堂/thumb/t_21.9.jpg;/images/台湾食堂/thumb/t_21.10.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (252, 9, 223, '', '月台便当', '福隆的曾阿发，将说另一个便当怎么传递一代又一代属于宜兰人的故事？', 22, '', '/images/台湾食堂/22.1.png;/images/台湾食堂/22.2.png;/images/台湾食堂/22.3.png', NULL, '/images/台湾食堂/thumb/t_22.1.jpg;/images/台湾食堂/thumb/t_22.2.jpg;/images/台湾食堂/thumb/t_22.3.jpg', NULL, NULL);
INSERT INTO `program_shop-1425` VALUES (253, 9, 224, '', '古早味便当', '奋起湖边，留着大胡子的林金坤讲起阿公的古早味，「把一件事做好，就对了！」他想传递的就是对奋起湖的浓浓感情。', 22, '', '/images/台湾食堂/22.4.png;/images/台湾食堂/22.5.png;/images/台湾食堂/22.10.png', NULL, '/images/台湾食堂/thumb/t_22.4.jpg;/images/台湾食堂/thumb/t_22.5.jpg;/images/台湾食堂/thumb/t_22.10.jpg', NULL, NULL);
